import fpgadbg_conv
def binary_to_hex_tuple(binary_string):
    hex_values = []
    for i in range(0, len(binary_string), 8):
        byte = binary_string[i:i+8]
        if len(byte) == 8:  # Ensure it's a full byte
            hex_values.append(hex(int(byte, 2)))
    return tuple(hex_values)

